# !pip install pyDOE

from pyDOE import lhs
import numpy as np
import re
import os


# Load the CSV file using numpy.loadtxt
csv_file = 'all-names-transpose.csv'  # Replace with the actual CSV file path
data = np.loadtxt(csv_file, dtype=str, delimiter=',')

# Extract valid points from the data
valid_points = set(data)

# Define the number of samples you want
num_samples = 170  # Adjust this as needed

# Define the ranges for the 2D grid
x_range = (1, 47)
y_range = (1, 23)

# Generate Latin hypercube samples for the 2D grid
samples = lhs(2, samples=num_samples, criterion='maximin')

# Rescale the samples to fit within the specified ranges
scaled_samples = []
for sample in samples:
    x = round(x_range[0] + sample[0] * (x_range[1] - x_range[0]))
    y = round(y_range[0] + sample[1] * (y_range[1] - y_range[0]))
    scaled_samples.append((x, y))

valid_samples = []
x_strings = []
y_strings = []
zone_name = []

# Print the generated samples
for i, (x, y) in enumerate(scaled_samples):
    point = f'small-box-{x:02d}-{y:02d}'
    #print(f"Sample {i + 1}: x = {x}, y = {y}")

    is_in_valid_points = any(point in valid_point for valid_point in valid_points)
    for valid_point in valid_points:
        if point in valid_point:
            #print(f"Sample {i + 1}: x = {x}, y = {y}")
            valid_samples.append((x, y))
            x_strings.append(str(x).zfill(2))
            y_strings.append(str(y).zfill(2))
            zone_name.append(valid_point)

print(len(zone_name))

np.savetxt('valid_samples_n_' + str(num_samples) + '.csv',valid_samples)
np.savetxt('zone_name_n_' + str(num_samples) + '.csv',zone_name,fmt='%s')



